mapper.select:
select b.* from (SELECT A.*, ROW_NUMBER() OVER(ORDER BY ROWNUM) RNUM FROM (select a.POST_NUM, a.POST_CATE_NUM, a.BLOGGER_ID, a.POST_TITLE, a.POST_CONTENT, a.POST_VIEW, a.POST_HEART, a.POST_DATE, a.POST_PUBLIC_YN, a.POST_TAG, a.POST_DEL_YN, a.THUMBNAIL, c.mem_nick from TB_BLOG_POST a left outer join tb_member c on(a.BLOGGER_ID=c.mem_id) where 1=1 and POST_PUBLIC_YN='Y' and POST_DEL_YN='N'
AND (INSTR(A.BLOGGER_ID, #{searchWord}) > 0 OR INSTR(A.POST_TITLE, #{searchWord}) > 0)
ORDER BY A.POST_DATE DESC ) A ) B WHERE RNUM BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE TB_BLOG_POST
SET POST_VIEW = POST_VIEW + 1
WHERE POST_NUM = #{post_num}
