mapper.select:
select b.* from (SELECT A.*, ROW_NUMBER() OVER(ORDER BY A.FREE_DATE DESC) RNUM FROM (select a.FREE_NUM , a.WRITER_ID , a.FREE_TITLE , a.FREE_CONTENT , to_char(a.FREE_DATE,'yy-mm-dd') FREE_DATE , a.REP_NUM , a.VIEW_NUM , c.mem_nick from TB_FREEBOARD a left outer join tb_member c on(a.writer_id=c.mem_id) where 1=1
AND (INSTR(A.WRITER_ID, #{searchWord}) > 0 OR INSTR(A.FREE_TITLE, #{searchWord}) > 0)
ORDER BY A.FREE_DATE DESC ) A ) B WHERE RNUM BETWEEN #{startRow} AND #{endRow}

mapper.update:
update tb_freeboard
set
free_title=#{freeTitle}
free_content=#{freeContent}
writer_id=#{writerId}
where free_num = #{freeNum}
